import { createSlice } from '@reduxjs/toolkit'

const initialState = [
  { id: '1', title: '1st Post', content: 'Excited!!' },
  { id: '2', title: '2nd Post', content: 'OMG not a good day' }
]

const postsSlice = createSlice({
  name: 'posts',
  initialState: [
    { id: '1', title: '1st Post', content: 'Excited!!' },
    { id: '2', title: '2nd Post', content: 'OMG not a good day' }
  ],
  reducers: {}
})

export default postsSlice.reducer
